import React, { useState } from "react";
import logo from "../../images/logo.png";
import "./Header.css";
import Bars from "../../assets/bars.png";
import { Link } from "react-scroll";
const Header = () => {
  const mobile = window.innerWidth <= 768 ? true : false;
  const [MenuOpened, setMenu] = useState(false);
  return (
    <div className="header">
      <img src={logo} alt="" className="logo" />
      {MenuOpened === false && mobile === true ? (
        <div onClick={() => setMenu(true)}>
          <img src={Bars} alt="" className="menubar" />
        </div>
      ) : (
        <ul className="header-menu">
          <li onClick={() => setMenu(false)}>
            <Link>Home</Link>
          </li>
          <li onClick={() => setMenu(false)}>Programs</li>
          <li onClick={() => setMenu(false)}>Why us</li>
          <li onClick={() => setMenu(false)}>Plans</li>
          <li onClick={() => setMenu(false)}>
            <Link to="Testimonlios" smooth={true} span={true}>
              Testimonials
            </Link>
          </li>
        </ul>
      )}
    </div>
  );
};
export default Header;
